import { Component } from '@angular/core';
import { Education } from 'src/app/model/education';
import { EducationService } from 'src/app/service/education-service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-education',
  templateUrl: './education.component.html',
  styleUrls: ['./education.component.css']
})
export class EducationComponent {
  education: Education[] = [];
  isLogged = false;

  modalEduUpd: boolean;

  constructor(private eduService: EducationService, private token: TokenService){}

  ngOnInit():void{
    this.loadEducation();

    if(this.token.getToken()){
      this.isLogged = true;
    }else{
      this.isLogged = false;
    }
  }

  loadEducation():void{
    this.eduService.getEdu().subscribe(data => {
      this.education = data;
    })
  }
  deleteEdu(id: number):void{
    if(id != undefined){
      this.eduService.deleteEdu(id).subscribe(data => {
        this.loadEducation();

      }, err => {
        alert("Erro al eliminar Educacion")
      })
    }
  }
}
