import { Component, OnInit } from '@angular/core';
import { About } from 'src/app/model/about';
import { Person } from 'src/app/model/person.model';
import { AboutService } from 'src/app/service/about.service';
import { ModalSwitchService } from 'src/app/service/modal-switch.service';
import { PersonService } from 'src/app/service/person.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent {
  person: Person = new Person("", "");
  about: About[];

  isLogged: boolean = false;

  modalAbouUp:boolean = false;

  constructor(public personService: PersonService, private tokenService: TokenService, 
    private $swithAbout: ModalSwitchService, private aboutService: AboutService){};

  ngOnInit(): void {
    //traer name and lastName
    this.personService.getPerson().subscribe(data=>{
      console.log(data);
      this.person = data
    })
    this.$swithAbout.$modal.subscribe((value)=>{
      this.modalAbouUp = value;
    })

    //traer tittle and description About

    if(this.tokenService.getToken()){
      this.isLogged = true;
    }else{
      this.isLogged = false;
    }
  };

  loadAbout():void{
    this.aboutService.getAbout().subscribe(data => {
      this.about = data;
    });
  }

}
