import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Languaje } from 'src/app/model/languaje';
import { LanguajeService } from 'src/app/service/languaje.service';
import { ModalSwitchService } from 'src/app/service/modal-switch.service';

@Component({
  selector: 'app-languaje-modal',
  templateUrl: './languaje-modal.component.html',
  styleUrls: ['./languaje-modal.component.css']
})
export class LanguajeModalComponent {

  languaje: string;
  lanjuageDomain: string;
  lanjuageDomainNumber: number;

  constructor(
    private $modalSwService: ModalSwitchService,
    private LanguajeService:LanguajeService,
    private router:Router){}

  loadLang(){
    let lang = new Languaje(this.languaje, this.lanjuageDomain, this.lanjuageDomainNumber);
    this.LanguajeService.saveLang(lang).subscribe((data)=>{
      alert("Lenguaje añadido");
      this.router.navigate(['']);
      window.location.reload();
    }, err => {
      alert("Fallo");
      this.router.navigate(['']);
      window.location.reload();
    }
    )
  }
  closeModalLang(){
    this.$modalSwService.$modal.emit(false);
  }
}
<div class="gx-0 gy-0 d-flex flex-column col-12">
    <div class="closeButton">
        <button class="rounded" (click)="closeModalLang()">X</button>
      </div>
      <div>
        <h2>
          Lenguaje
        </h2>
      </div>
      <form #f="ngForm" (ngSubmit)="loadLang()" novalidate>
        <div class="form-floating mb-3">
          <input type="text" class="form-control" id="languaje" name="languaje" [(ngModel)]="languaje" required placeholder="Ej: "/>
          <label for="languaje">Lenguaje</label>
        </div>
        <div class="form-floating">
          <input type="text" class="form-control" id="lanjuageDomain" name="lanjuageDomain" [(ngModel)]="lanjuageDomain" required placeholder="">
          <label for="lanjuageDomain">Dominio del lenguaje</label>
        </div>
        <div class="form-floating">
            <input type="text" class="form-control" id="lanjuageDomainNumber" name="lanjuageDomainNumber" [(ngModel)]="lanjuageDomainNumber" required placeholder="">
            <label for="lanjuageDomainNumber">Dominio del lenguaje en porcentaje</label>
          </div>
        <div class="">
          <button type="submit" class="btn btn-primary">Crear</button>
        </div>
      </form>
</div>
