import { Component } from '@angular/core';
import { Experience } from 'src/app/model/experience';
import { ExperienceServiceService } from 'src/app/service/experience-service.service';
import { ModalSwitchService } from 'src/app/service/modal-switch.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-experience',
  templateUrl: './experience.component.html',
  styleUrls: ['./experience.component.css']
})
export class ExperienceComponent {
  experience: Experience[] = [];

  modalExp!:boolean;

  constructor(private expService: ExperienceServiceService,  private tokenService: TokenService, private $swithExp: ModalSwitchService){}

  isLogged = false;
  ngOnInit(){
    this.$swithExp.$modal.subscribe((value)=>{
      this.modalExp = value;
    })
    this.loadExp()
    if(this.tokenService.getToken()){
      this.isLogged = true;
    }else{
      this.isLogged = false;
    }
  }

  loadExp():void{
    this.expService.getExperiences().subscribe(data=>{
      this.experience = data;
    })
  }

  openModalExp():void{
    this.modalExp = true;
  }
}
