import { Component } from '@angular/core';
import { Project } from 'src/app/model/project';
import { ModalSwitchService } from 'src/app/service/modal-switch.service';
import { ProjectService } from 'src/app/service/project.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.css']
})
export class ProjectsComponent {
  isLogged:boolean;
  project: Project [] = [];
  modalPrj!:boolean;
  modalPrjUpd!:boolean;
  Fdate:string;

  constructor(private tokenService: TokenService, private prjService: ProjectService, private $swithPrj: ModalSwitchService){}

  ngOnInit():void{
    this.$swithPrj.$modal.subscribe((value)=>{
      this.modalPrj = value;
    });
    this.loadPrj();
    if(this.tokenService.getToken()){
      this.isLogged = true;
    }else{
      this.isLogged = false;
    }
  }
  loadPrj(){
    this.prjService.getProject().subscribe(data => {
      this.project = data;
    });
  }
  // expUpdId!:boolean;
  openModalPrj():void{
    this.modalPrj = true;
  }

  openModalExpUpd(id:number){
  }

  deletePrj(id: number):void{
    if(id != undefined){
      this.prjService.deletePrj(id).subscribe(data => {
        this.loadPrj();
      }, err => {
        alert("Error al eliminar El proyecto")
      })
    }
  }
  convertDate(ISODate:Date):string{
    let newDate = new Date(ISODate);
    let day = newDate.getDate();
    let month = newDate.getMonth()+1;
    let year = newDate.getFullYear();
    this.Fdate = `${day}/${month}/${year}`
    console.log(this.Fdate);
    return this.Fdate;
  }
}
