import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Experience } from 'src/app/model/experience';
import { ExperienceService } from 'src/app/service/experience-service';
import { ModalSwitchService } from 'src/app/service/modal-switch.service';

@Component({
  selector: 'app-update-exp',
  templateUrl: './update-exp.component.html',
  styleUrls: ['./update-exp.component.css']
})
export class UpdateExpComponent {
  expUp: Experience = null;
  switchExp!: boolean;
  expUdaId!: number;

  constructor(private experienceService: ExperienceService, private activatedRoute: ActivatedRoute, 
    private router: Router, private $modalSwService: ModalSwitchService, private $switchId: ModalSwitchService){}
  ngOnInit():void{

    this.$switchId.$modal.subscribe((value)=>{
      console.log(value);
      this.expUdaId = value;
    });
    console.log(this.expUdaId);

    // const id = this.activatedRoute.snapshot.params['id'];

    this.experienceService.getExpById(this.expUdaId).subscribe(data => {
      this.expUp = data;
    }, err => {
      alert("Fallo la actualización");
      this.router.navigate(['']);
    });
  }

  updateExp():void{
    const id = this.activatedRoute.snapshot.params['id'];
    console.log(this.expUdaId);

    this.experienceService.updateExp(this.expUdaId, this.expUp).subscribe( data => {
      this.router.navigate(['']);
    }, err => {
      alert("Fallo la actualización");
      this.router.navigate(['']);
    });
  }

  closeModalExpUpd():void{
    this.$modalSwService.$modal.emit(false);
  }
}
